package assignment;

public class Productofnegativeintegers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, num =10, product =1;  

		for(i = 1; i<=num; ++i)  
		{  
		  
		product = product*i;  
		}  

		System.out.println("product of negative integer is = " + product); 
	}

}
